import { useEffect, useState } from "react";
import HeadNav from "../Common/HeadNav";
import CandidateRow from "./CandidateRow";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";
import "../Common/Styles.css";
import axios from '../DataAccess/AxiosTemp'
function SearchCandidate() {






    const NameChange = (args) => {
        var copyofname = { ...FirstName };

        copyofname[args.target.name] = args.target.value;
        setFirstName(copyofname);
    }

    const history = useHistory();

    const ViewCandidate = () => {
        history.push("/CandidateProfile");
    }

    const [AllCandidate, setAllCandidate] = useState([
        {
            firstName: "",
            userId: "",
            lastName: "",
            email: "",
            occupation: ""
        }
    ]);

    const [FirstName,setFirstName] =  useState("firstName");

    const FirstNameChange = (args) => {
        var copyofname = { ...FirstName };

        copyofname[args.target.name] = args.target.value;
        setFirstName(copyofname);
      }



    useEffect(async () => {

        await axios.post('/candidate/searchByName', FirstName, config)
            .then(response => {
                setAllCandidate(response.data)
            })
            .catch(error => {
                // Handle errors here
                console.error('Error:', error);
            });

    }, [FirstName]);



    return <>


        <HeadNav></HeadNav>


        <div class="jumbotron" style={{ marginTop: "-20px" }}>
            <div class="container">
                <center>
                    <p>Search Candidate</p>
                </center>
            </div>
        </div>

        <div className="col-lg-3" ></div>
        <div className="col-lg-6" >
            <center>
                <input type="text" placeholder="Enter The Name or Region" className="custom-select" value={FirstName.firstName} name="firstName" onChange={FirstNameChange}></input>
                <br></br><br></br><br></br>
                <button className="btn btn-primary btn-lg" onClick={SearchCandidate}>Search Here</button>
            </center>
        </div>

        {/* 
        <div className="container-fluid text-center">

            <div className="row content">
                <div className="col-lg-12 sidenav">
                    <br></br><br></br>
                    <table className='table table-responsive'>
                        <tbody>
                            {
                                AllCandidate.map((candidate) => {
                                    return <CandidateRow key={candidate.FirstName}
                                        candidate={candidate}
                                        ViewCandidate={ViewCandidate}
                                    />
                                })
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        </div> */}

        <div className="container-fluid text-center">
            <div className="row content">
                <div className="col-lg-12 sidenav">
                    <br /><br />
                    <div className="table-responsive">
                        <table className='table table-bordered table-striped'>
                            <thead className="thead-dark">
                                <tr>

                                    <th>First Name</th>
                                    <th>Last Name</th>
                                    <th>Email</th>
                                    <th>Phone</th>
                                    {/* Add more table headings as needed */}
                                    {/* <th>Actions</th> */}
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    AllCandidate.map((candidate) => (
                                        <CandidateRow key={candidate.email} candidate={candidate} ViewCandidate={ViewCandidate} />
                                    ))
                                }
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        </div>



    </>
}

export default SearchCandidate;
