import "../Common/Styles.css"
import { useState } from 'react';
import axios from '../DataAccess/AxiosTemp';

function Login() {

    const [loggedUser, setloggedUser] = useState({
        token: "",
        type: "",
        id: "",
        firstName: "",
        lastName: "",
        email: "",
        userType: ""
    })


    const history = useHistory();


    // const LoggedIn = () => {

