import React, { useEffect, useState } from 'react';
import { Route } from 'react-router-dom';
import AdminHome from '../../Admin/AdminHome/AdminHome';
import UserHome from '../../UserHome/UserHome';
import CandidateProfileForCandidate from '../../CandidateProfile/CandidateProfileForCandidate';
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';

function ProtectedRoutesForLogin(Props) {
    const history = useHistory();



        const [loggedUser, setloggedUser] = useState({
                token: "",
                type: "",
                id: "",
                firstName: "",
                lastName: "",
                email: "",
                userType: ""
            })

    useEffect(() => {
        // Function to get the user data from cookies
        const cookies = document.cookie.split('; ');
                for (const cookie of cookies) {
                    const [name, value] = cookie.split('=');
                    if (name === 'loggedUser') {
                        const user = JSON.parse(decodeURIComponent(value));
                        setloggedUser(user);
                        break;
                    }
                }

        // Read the user data from the 'loggedUser' cookie when the component mounts
        const userFromCookie = getCookie('loggedUser');
        if (userFromCookie) {
            set(userFromCookie);
        } else {
            // Redirect to the login page if no user data is found in cookies
            history.push("/Login");
        }
    }, [history]);

    debugger;
    if (loggedUser && loggedUser.userType === "ADMIN") {
        return <Route exact path={Props.path} component={AdminHome}></Route>
    } else if (loggedUser && loggedUser.userType === "VOTER") {
        return <Route exact path={Props.path} component={UserHome}></Route>
    } else if (loggedUser && loggedUser.userType === "CANDIDATE") {
        return <Route exact path={Props.path} component={CandidateProfileForCandidate}></Route>
    } else {
        return null; // Return null if no match is found (no re-routing here)
    }
}

export default ProtectedRoutesForLogin;
