import { useEffect, useState } from "react";
import HeadNav from "../Common/HeadNav";
import CandidateRow from "./CandidateRow";
import { useHistory } from "react-router-dom/cjs/react-router-dom.min";
import "../Common/Styles.css";

function SearchCandidate() {


    var [Name,setName] = useState("name");

    useEffect(() => {

        const getName = sessionStorage.getItem("SearchingCandidateName");
        Name = JSON.parse(getName);
    }, []);

    const NameChange = (args) => {
        var copyofname = { ...Name };

        copyofname[args.target.name] = args.target.value;
        setName(copyofname);
    }

    const history = useHistory();

    const ViewCandidate = () => {
        history.push("/CandidateProfile");
    }

    const [AllCandidate, setAllCandidate] = useState([
        {
          firstName: "",
          userId: "",
          lastName: "",
          email: "",
          occupation: ""
        }
      ]);





    return <>


        <HeadNav></HeadNav>


        <div class="jumbotron" style={{marginTop:"-20px"}}>
            <div class="container">
                <center>
                    <p>Search Candidate</p>
                </center>
            </div>
        </div>

        <div className="col-lg-3" ></div>
        <div className="col-lg-6" >
            <center>
                <input type="text" placeholder="Enter The Name or Region" className="custom-select" name="name" value={Name.name} onChange={NameChange}></input>
                <br></br><br></br>
                <button className="btn btn-primary btn-lg"  onClick={SearchCandidate}>Search Here</button>
            </center>
        </div>

